import { buttonGroupClasses } from '@mui/material';
import React, { useContext } from 'react'
import { AppContext } from '../context/AppContext'

const Metamask = () => {
    const { appStatus, ConnectToWallet } = useContext(AppContext);
    console.log(appStatus)
    const app = (status = appStatus) => {
        switch (status) {
            case 'connected':
                return userLoggedIn

            case 'not-Connected':
                return noUserFound

            case 'noMetaMask':
                return noMetamaskFound

            case 'error':
                return error

            default:
                return loading
                break;
        }
    }
    const noUserFound = (
        <button onClick={() => ConnectToWallet()}>Connect to wallet</button>
    )
    const userLoggedIn = (
        <div>Connected</div>
    )
    const noMetamaskFound = (
        <div className=''>
            You must install Metamask, a <br /> virtual Ethereum wallet, in your browser
        </div>
    )
    const error = (
        <div>An error occurred. Please try again later or use another browser</div>
    )
    const loading = (
        <div> Loading... </div>
    )
    return (
        <div>{app(appStatus)}</div>
    )
}

export default Metamask
