import { useRef, useState } from 'react';

import { MenuItem, FormControl, Select, TextField, Box, Button, Container } from '@mui/material'
import { handleArrange, handleFilters } from '../../app/actions';
import { useDispatch, useSelector } from 'react-redux';
export const InputSelect = ({ getFilter }) => {
    const dispatch = useDispatch();
    const filters = useSelector(state => state.filters)

    const [arrange, setArrange] = useState(filters.arrange);
    const [minScore, setMinScore] = useState(filters.minScore);
    const [level, setLevel] = useState(filters.level);

    const selectRef = useRef()
    const handleLevelChange = (e) => {
        setLevel(e.target.value);
    }

    const handleMinScoreChange = (e) => {
        setMinScore(e.target.value);
    }

    const ArrangeChange = (event) => {
        setArrange(event.target.value);
        // console.log(event.target.value)
        dispatch(handleArrange(event.target.value));
    };
    const FiltersChange = () => {
        dispatch(handleFilters({ minScore, level }))
    }
    return (
        <Container maxWidth="lg" sx={{ marginTop: '20px' }}>
            <Box sx={{
                display: 'flex',
                alignItems: 'end'
            }}>
                <TextField value={minScore} onChange={handleMinScoreChange} sx={{ width: 100, marginRight: '0.5rem' }} id="min-score" label="Min score" variant="standard" type='number' />
                <TextField value={level} onChange={handleLevelChange} sx={{ width: 100, marginRight: '0.5rem' }} id="level" label="Level" variant="standard" type='number' />
                <Button sx={{ height: '100%' }} sx={{ color: '#383838', background: '#fcc33c' }} variant="contained" onClick={FiltersChange}>Confirm</Button>
            </Box>
            <Box>
                <FormControl style={{ margin: '20px 0' }}>
                    <Select
                        ref={selectRef}
                        size='small'
                        labelId="select"
                        id="filter"
                        value={arrange}
                        onChange={ArrangeChange}
                    >
                        <MenuItem value={0}>Lowest Price</MenuItem>
                        <MenuItem value={1}>Highest Level</MenuItem>
                        <MenuItem value={2}>Highest Score</MenuItem>
                    </Select>


                </FormControl>
            </Box>

        </Container>

    )

}
