import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import './App.css';
import { updatePriceAuto } from './app/actions';
import RacaTabs from './components/Tabs';
import axios from "axios"




function App() {

  const dispatch = useDispatch();

  const getPrice = async () => {
    await axios.get('https://api.coingecko.com/api/v3/simple/price?ids=radio-caca&vs_currencies=usd').then(res => {
      const price = res.data['radio-caca'].usd
      document.title = price + " - Marketplace RACA"
      dispatch(updatePriceAuto(price))
    });
  }
  useEffect(() => {
    getPrice()
    const updatePrice = setInterval(async () => {
      getPrice()
    }, 20000)

    return () => {
      clearInterval(updatePrice)
    }
  }, [])
  return (
    <div className="App">
      <RacaTabs />
    </div>

  );
}

export default App;
