import React, { useContext, useEffect, useState } from 'react'
import Metamask from './Metamask'
import { TextField, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, CircularProgress, Table } from '@mui/material';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const MarketHistory = () => {
  const [loading, setLoading] = useState(false);
  const { currentAccount } = useContext(AppContext);
  const [historyList, setHistoryList] = useState([])
  console.log(currentAccount)
  const getMarketHistory = (pageNo, pageSize) => {
    return axios(`https://market-api.radiocaca.com/users/${currentAccount}/histories?pageNo=${pageNo}&pageSize=${pageSize}`).then(res => res.data)
  }
  useEffect(() => {
    const getHistoryData = async () => {
      const pageNo = 1;
      let pageSize = 95;
      getMarketHistory(pageNo, pageSize).then(res => { // total = 200
        setHistoryList(res.list); 
        let lengthList = res.list.length; //95
        while (lengthList < res.total) {// 95<200
          pageNo++; //3
          pageSize = res.total - lengthList > 95 ? 95 : res.total - lengthList; // 200-190 = 10
          getMarketHistory(pageNo, pageSize).then(res => { //pageSize 10
            lengthList += res.list.length; //95+95 = 190
            setHistoryList(prev => prev.concat(res.list));
          })
        }
      })
    }
    getHistoryData();
  }, [currentAccount])

  return (
    <div>
      <Metamask />
      <div className="sales">
      {console.log(historyList)}
        <TableContainer id="sales-section" component={Paper}>
          <Table sx={{ minWidth: 650, minHeight: 150 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align='center'>NFT</TableCell>
                <TableCell align="center">Buy</TableCell>
                <TableCell align="center">Sell</TableCell>
                <TableCell align="center">Summary</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              <TableRow >
                <TableCell colSpan={4} row align='center'>
                  {loading && <CircularProgress color="primary" />}
                </TableCell>
              </TableRow>
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    </div>
  )
}

export default MarketHistory
