import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import './App.css';
import { updatePriceAuto } from './app/actions';
import HeaderTabs from './components/Tabs';
import axios from "axios"
import { Route, Switch, Redirect } from "react-router-dom";




function App() {

  const dispatch = useDispatch();

  const getPrice = async () => {
    const raca = await axios.get('https://api.pancakeswap.info/api/v2/tokens/0x12bb890508c125661e03b09ec06e404bc9289040').then(res => Number(res.data.data.price).toFixed(8));
    const elmon = await axios.get('https://api.coingecko.com/api/v3/simple/price?ids=elemon&vs_currencies=usd').then(res => res.data['elemon'].usd);
    const elcoin = await axios.get('https://api.pancakeswap.info/api/v2/tokens/0x092ffbc968203b652b08361adec75e275573f2db').then(res => Number(res.data.data.price).toFixed(8));
    document.title = raca + " - Marketplace RACA"
    dispatch(updatePriceAuto({ raca, elmon, elcoin }))
  }
  useEffect(() => {
    getPrice()
    const updatePrice = setInterval(async () => {
      getPrice()
    }, 20000)

    return () => {
      clearInterval(updatePrice)
    }
  }, [])
  return (
    <div className="App">
      <HeaderTabs />
    </div>

  );
}

export default App;
