import { buttonGroupClasses } from '@mui/material';
import React, { useContext } from 'react'
import { AppContext } from '../context/AppContext'

const Metamask = () => {
    const { appStatus, connectToWallet, currentAccount } = useContext(AppContext);
    console.log(appStatus)
    const app = (status = appStatus) => {
        switch (status) {
            case 'connected':
                return userLoggedIn

            case 'not-connected':
                return noUserFound

            case 'noMetamask':
                return noMetamaskFound

            case 'error':
                return error

            default:
                return loading
        }
    }
    const noUserFound = (
        <div>
            <img src='/marketplaceraca/public/metamask.png' />
            <button onClick={() => connectToWallet()}>Connect to wallet</button>

        </div>
    )
    const userLoggedIn = (
        <div>{currentAccount}</div>
    )
    const noMetamaskFound = (
        <div className=''>
            You must install Metamask, a <br /> virtual Ethereum wallet, in your browser
        </div>
    )
    const error = (
        <div>An error occurred. Please try again later or use another browser</div>
    )
    const loading = (
        <div> Loading... </div>
    )
    return (
        <div>{app(appStatus)}</div>
    )
}

export default Metamask
