import { createContext, useEffect, useState } from 'react'
import useRouter from './useRouter'
export const AppContext = createContext();

export const AppProvider = ({ children }) => {
    const [appStatus, setAppStatus] = useState('loading')
    const [currentAccount, setCurrentAccount] = useState('');

    useEffect(() => {
        checkIfWalletConnected();
        window.ethereum.on('accountsChanged', async () => {
            const getCurrentAccount = await window.ethereum.request({
                method: 'eth_accounts',
            })
            setCurrentAccount(getCurrentAccount[0]);
        })
    }, [])

    const checkIfWalletConnected = async () => {
        if (!window.ethereum) return setAppStatus('noMetamask')
        try {
            const addressArray = await window.ethereum.request({
                method: 'eth_accounts',
            })

            if (addressArray.length > 0) {
                //connected
                setAppStatus('connected');
                setCurrentAccount(addressArray[0]);
            } else {
                // not connected

                setAppStatus('not-connected');
            }
        } catch (err) {
            console.log(err);
        }
    }

    const connectToWallet = async () => {
        try {
            setAppStatus('loading');
            // console.log('asdsad')
            // console.log(window.ethereum);
            const addressArray = await window.ethereum.request({
                method: 'eth_requestAccounts',
            })
            if (addressArray.length > 0) {
                setCurrentAccount(addressArray[0])
            }else{
                
            }
        } catch (err) {
            console.log(err);
        }
    }

    return (
        <AppContext.Provider value={{ appStatus, currentAccount, connectToWallet }}>{children}</AppContext.Provider>
    )
}
