import { useState } from 'react';
import './App.css';
import PriceTable from './components/PriceTable';
import Sales from './components/Sales';
function App() {
  const [currentSection, setCurrentSection] = useState('NFT');
  const [toggle,setToggle] = useState(false);
  const handleClick = (e) => {
    const nft = document.getElementById('NFT');
    const sales = document.getElementById('Sales');
    const metamon = document.getElementById('metamon');

    if (e.target.id !== currentSection) {
      setToggle(preState=> !preState);
      setCurrentSection(e.target.id)
      console.log(currentSection);

    }
  }

  return (
    <div className="App">
      <div className="section">
        <button onClick={e => handleClick(e)} id="NFT">NFTs</button> |
        <button onClick={e => handleClick(e)} id="sales">Sales</button>
        <button onClick={e => handleClick(e)} id="metamon">Metamon Islands</button>
      </div>
      {/* {currentSection === "NFT" ? <PriceTable hidden={true} /> : <Sales />} */}
      <PriceTable hidden={toggle} /> 
      <Sales hidden={!toggle}/>
    </div>

  );
}

export default App;
