import React, { useContext, useEffect, useState } from 'react'
import Metamask from './Metamask'
import { TextField, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, CircularProgress, Table } from '@mui/material';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const MarketHistory = () => {
  const [loading, setLoading] = useState(false);
  const { currentAccount } = useContext(AppContext);
  const [historyList, setHistoryList] = useState([]);
  const [length, setLength] = useState(0);
  const [totalBuy, setTotalBuy] = useState('');
  const [totalSell, setTotalSell] = useState('');
  const getMarketHistory = (pageNo, pageSize) => {
    return axios(`https://market-api.radiocaca.com/users/${currentAccount}/histories?pageNo=${pageNo}&pageSize=${pageSize}`).then(res => res.data)
  }
  useEffect(() => {
    getMarketHistory(2,2).then(res=>console.log(res))
    const getHistoryData = async () => {
      let pageNo = 1;
      let pageSize = 10;
      // getMarketHistory(pageNo, pageSize).then(async (res) => {
      //   setLength(res.total);
      //   console.log(res)
      //   setHistoryList(pre => pre.concat(res.list));
      //   for (let i = 2; i <= Math.floor(res.total / 10) + 1; i++) {
      //     if (i == 10)
      //       continue;
      //     const listData = await getMarketHistory(i, pageSize);
      //     setHistoryList(pre => pre.concat(listData.list))
      //   }
      // })
    }

    if (currentAccount)
      getHistoryData();

    return () => {
      setHistoryList([])
    }
  }, [currentAccount])
  useEffect(() => {
    // console.log(historyList)
    const listNFT = [];
    const listBuy = [];
    const listSell = [];
    let buy = 0;
    let sell = 0;
    // console.log(historyList.length,length)
    if (historyList.length == (length - 10) && historyList.length != 0) {
      historyList.forEach(item => {

        if (item.type == 'buy') {
          listBuy.push(item);
          buy += item.amount
        }
        else {
          listSell.push(item)
          sell += item.amount + item.fee
        }
      })
      console.log(listNFT)
    }

  }, [historyList])

  return (
    <div>
      <Metamask />
      <div className="sales">
        <TableContainer id="sales-section" component={Paper}>
          <Table sx={{ minWidth: 650, minHeight: 150 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align='center'>NFT</TableCell>
                <TableCell align="center">Buy</TableCell>
                <TableCell align="center">Sell</TableCell>
                <TableCell align="center">Summary</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              <TableRow >
                <TableCell colSpan={4} align='center'>
                  {loading && <CircularProgress color="primary" />}
                </TableCell>
              </TableRow>
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    </div>
  )
}

export default MarketHistory
