import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import './App.css';
import { updatePriceAuto } from './app/actions';
import RacaTabs from './components/Tabs';
import axios from "axios"




function App() {

  const dispatch = useDispatch();

  const getPrice = async () => {
    const raca = await axios.get('https://api.coingecko.com/api/v3/simple/price?ids=radio-caca&vs_currencies=usd').then(res => res.data['radio-caca'].usd);
    const elmon = await axios.get('https://api.coingecko.com/api/v3/simple/price?ids=elemon&vs_currencies=usd').then(res => res.data['elemon'].usd);

    document.title = raca + " - Marketplace RACA"
    dispatch(updatePriceAuto({ raca, elmon }))
  }
  useEffect(() => {
    getPrice()
    const updatePrice = setInterval(async () => {
      getPrice()
    }, 20000)

    return () => {
      clearInterval(updatePrice)
    }
  }, [])
  return (
    <div className="App">
      <RacaTabs />
    </div>

  );
}

export default App;
