import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import './App.css';
import { updatePriceAuto } from './app/actions';
import RacaTabs from './components/Tabs';
import axios from "axios"


const getPrice = async () => {
  Promise.all([
    getAPI(13, 10).then(res =>
      setListMetamon(res.data.list)
    ),
    getAPI(15, 10).then(res =>
      setListPotion(res.data.list)
    ),
    getAPI(16, 10).then(res =>
      setListDiamond(res.data.list)
    ),
    getAPI(17, 10).then(res =>
      setListEgg(res.data.list)
    ),
    getAPI(20, 1).then(res => {
      setKissUpLand(res.data.list[0]);
      document.querySelector('.loading')?.classList.toggle('loading');
    }),

  ])
  return await axios.get('https://api.coingecko.com/api/v3/simple/price?ids=radio-caca&vs_currencies=usd').then(res => res.data['radio-caca'].usd)
}
function App() {

  const dispatch = useDispatch();

  useEffect(() => {

    const updatePrice = setInterval(async () => {
      const price = await getPrice()
      document.title = price + " - Marketplace RACA"
      dispatch(updatePriceAuto(price))
    }, 20000)

    return () => {
      clearInterval(updatePrice)
    }
  }, [])
  return (
    <div className="App">
      <RacaTabs />
    </div>

  );
}

export default App;
