import { useState } from 'react';

import { MenuItem, FormControl, Select, TextField, Box, Button, Container } from '@mui/material'
import { handleArrange } from '../../app/actions';
export const InputSelect = ({ getFilter }) => {
    const [arrange, setArrange] = useState();
    const [minScore, setMinScore] = useState();
    const [level, setLevel] = useState();
    const dispatch = useDispatch();
    const handleChange = (event) => {
        setArrange(event.target.value);
        getFilter(event.target.value);
        dispatch(handleArrange(event.target.value));
    };
    
    return (
        <Container maxWidth="lg" sx={{ marginTop: '20px' }}>
            <Box sx={{
                display: 'flex',
                alignItems: 'end'
            }}>
                <TextField sx={{ width: 100, marginRight: '0.5rem' }} id="min-score" label="Min score" variant="standard" />
                <TextField sx={{ width: 100, marginRight: '0.5rem' }} id="level" label="Level" variant="standard" />
                <Button sx={{ height: '100%' }} sx={{ color: '#383838', background: '#fcc33c' }} variant="contained">Confirm</Button>
            </Box>
            <Box>
                <FormControl style={{ margin: '20px 0' }}>
                    <Select
                        size='small'
                        labelId="select"
                        id="filter"
                        value={arrange}
                        onChange={handleChange}
                    >
                        <MenuItem value={0}>Lowest Price</MenuItem>
                        <MenuItem value={1}>Highest Level</MenuItem>
                        <MenuItem value={2}>Highest Score</MenuItem>
                    </Select>


                </FormControl>
            </Box>

        </Container>

    )

}
