import axios from 'axios';
import React from 'react';
import { useEffect } from 'react';
import InputFilters from './InputFilters';
import '../../styles/elemon.css';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Box, cardClasses, Grid } from '@mui/material';
import InfoCard from './InfoCard';
const getElemonItem = (pageNumber = 1, pageSize = 20, priceMode = 2, baseCardId, tokenId, rarities, classes, purities) => {
    return axios.get(`https://app.elemon.io/market/getElemonItems?pageNumber=${pageNumber}&pageSize=${pageSize}&positionType=2&priceMode=${priceMode}&baseCardId=&tokenId=&rarities=&classes=&purities=`).then(res => res.data, {
        headers: {
            "Access-Control-Allow-Origin": "*"
        }
    });
}
const getElemonInfo = (listElemon, amount) => {
    const listTokenId = listElemon.splice(1, amount).map(token => token.tokenId)
    return axios.get(`https://app.elemon.io/elemon/GetElemonInfo?tokenId=${listTokenId.join(',')}`).then(res => res.data, {
        headers: {
            "Access-Control-Allow-Origin": "*"
        }
    });
}

const Elemon = () => {
    const initMetamon = {
        image: '',

    }
    useEffect(() => {
        let pageNumber = 1;
        const getData = async () => {
            await getElemonItem(pageNumber, 100).then(async (res) => {
                // console.log(res.paging.totalCount, res.data[0].tokenId)
                const list = await getElemonItem(1, res.paging.totalCount).then(res => res.data);
                console.log(list)

                const listInfo = await getElemonInfo(list, 10).then(res => res.data);
                console.log(listInfo)
            });
        }
        getData();
    })

    return (
        <Box>
            <Box sx={{ textAlign: 'left' }}>
                <InputFilters />
                {/* <TableContainer id='table-scroll' component={Paper}>
                    <Table id='tablePrice' aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell align="center">#</TableCell>
                                <TableCell align="center">

                                </TableCell>
                                <TableCell align="center">
                                    ID
                                </TableCell>
                                <TableCell align="center">
                                    Score
                                </TableCell>
                                <TableCell align="center">
                                    Level
                                </TableCell>
                                <TableCell align="center" sx={{ width: '25%' }}>
                                    Price
                                </TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>

                        </TableBody>
                    </Table>
                </TableContainer> */}
                <Grid container spacing={2}>
                    <Grid item xs={6} md={3} lg={1}>
                        <InfoCard />
                    </Grid>

                    <Grid item xs={6} md={3} lg={1}>
                        <InfoCard />
                    </Grid>
                    <Grid item xs={6} md={3} lg={1}>
                        <InfoCard />
