import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

const initState = {
    filters: {
        minScore: 315,
        level: 20,
        arrange: 0,
    }

}

const baseUrl = 'https://market-api.radiocaca.com/nft-sales'
const createRequest = (url) => ({ url })
export const priceApiCGC = createApi({
    reducerPath: 'priceApiCGC',
    baseQuery: fetchBaseQuery({ baseUrl }),
    endpoints: (builder) => ({
        getPrice: builder.query({
            query: (categoryId, size=1) => createRequest(`?pageSize=${size}&sortBy=fixed_price&order=asc&category=${categoryId}&tokenId=-1`)
        })
    })
})

const rootReducer = (state = initState, action) => {
    switch (action.type) {
        case 'handleFilters':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    minScore: action.payload.minScore,
                    level: action.payload.level
                }
            }
        case 'handleArrange':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    arrange: action.payload
                }
            }
        default:
            return state;
    }
}
export default rootReducer;

export const { useGetNFTsQuery } = marketplaceAPI
