const initState = {
    filters: {
        minScore: 315,
        level: 20,
        arrange: 0,
    }

}

const rootReducer = (state = initState, action) => {
    switch (action.type) {
        case 'handleFilters':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    minScore: action.payload.minScore,
                    level:action.payload.level
                }
            }
        case 'handleArrange':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    arrange: action.payload
                }
            }
        default:
            return state;
    }
}
export default rootReducer;
