import axios from "axios"

export const handleFilters = ({ minScore, level }) => {
    return {
        type: 'handleFilters',
        payload: {
            minScore,
            level
        }
    }
}

export const handleArrange = (arrange) => {
    return {
        type: 'handleArrange',
        payload: arrange
    }
}

export const updatePrice = async () => {
    return {
        type: 'updatePrice',
        payload: await axios.get('https://api.coingecko.com/api/v3/simple/price?ids=radio-caca&vs_currencies=usd')
            .then(res => res.data['radio-caca'].usd)
    }
}
