const initState = {
    filters: {
        minScore: 315,
        level: 20,
        arrange: true,
    }

}

const rootReducer = (state = initState, action) => {
    switch (action.type) {
        case 'handleMinScore':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    minScore: action.payload
                }
            }
        case 'handleLevel':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    level: action.payload
                }
            }
        case 'handleArrange':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    arrange: action.payload
                }
            }
        default:
            return state;
    }
}
export default rootReducer;
