const initState = {
    filters: {
        minScore: 315,
        level: 20,
        arrange: 0,
    },
    price: {
        raca: 0,
        elmon: 0
    },
    filtersElemon: {
        sort: 0,
        name: '',
        id: '',
        rarity: '',
        purity: '',
        price: '',
        min: 160000,
        max: 200000,
    }
}

const rootReducer = (state = initState, action) => {
    switch (action.type) {
        case 'handleFilters':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    minScore: action.payload.minScore,
                    level: action.payload.level
                }
            }
        case 'handleArrange':
            return {
                ...state,
                filters: {
                    ...state.filters,
                    arrange: action.payload
                }
            }
        case 'handlePower':
            return {
                ...state,
                filtersElemon: {
                    ...state.filtersElemon,
                    min: action.payload.min,
                    max: action.payload.max,
                }
            }
        case 'handleSort':
            return {
                ...state,
                filtersElemon: {
                    ...state.filtersElemon,
                    sort: action.payload.sort
                }
            }
        case 'updatePrice':
            return {
                ...state,
                price: action.payload
            }
        default:
            return state;
    }
}
export default rootReducer;
