import { useEffect, useState } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { numberWithCommas } from './NFTs/PriceTable'
import axios from 'axios';
function createData(name, sales, cost, profit, withdraw, remain) {
    return { name, sales, cost, profit, withdraw, remain };
}

const getHistory = (page, size) => {
    return axios.get(`https://market-api.radiocaca.com/users/0x769ba0cb0d89666f7506194d2cf416ea0f812e16/histories?pageNo=${page}&pageSize=${size}`);

}

const rows = [
    createData('Johny Duc', 69290, 202200, 132911, 115252.4, 17658),
    createData('Khang Pug', 69290, 202200, 132911, 115252.4, 17658),
    createData('Duc Professor', 34645, 101100, 66455, 115252.4, 8829),
    createData('Total', 173224, 505501, 332277, 288131, 44146),

];

const Sales = () => {
    const [listHistory, setListHistory] = useState([]);

    useEffect(() => {
        let total = 0;
        let currentPage = 1;
        let listTemp = [];
        let size = 10;
        const fetchData = async () => {
            do {
                console.log("start")
                console.log(response.data.total, listTemp.length, currentPage, size)
                const response = await getHistory(currentPage, size)
                currentPage++;
                size = currentPage * 10 >= total ? total - currentPage * 10 - 10: 10;
                total = response.data.total;
                listTemp = [...listTemp, ...response.data.list]
                console.log(listTemp)
            } while (listTemp.length <= total)
        }
        fetchData();
        return (
            setListHistory([])
        )
    }, [])
    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>Name</TableCell>
                        <TableCell align="center">Sales</TableCell>
                        <TableCell align="center">Cost</TableCell>
                        <TableCell align="center">Profit</TableCell>
                        <TableCell align="center">Withdraw</TableCell>
                        <TableCell align="center">Remain</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row) => (
                        <TableRow
                            key={row.name}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                        >
                            <TableCell component="th" scope="row">
                                {row.name}
                            </TableCell>
                            <TableCell align="center">{numberWithCommas(row.sales)}</TableCell>
                            <TableCell align="center">{numberWithCommas(row.cost)}</TableCell>
                            <TableCell align="center">{numberWithCommas(row.profit)}</TableCell>
                            <TableCell align="center">{numberWithCommas(row.withdraw)}</TableCell>
                            <TableCell align="center">{numberWithCommas(row.remain)}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}

export default Sales;
